    removePosInvoice: function (index, row, rows) {
      var vm = this;
      var companyDoc = WB_waterBillingSetup.findOne({
        rolesArea: Session.get("area")
      });

      if (companyDoc.integratedPosAccount) {
        Meteor.call("queryLastClosingEntry", Session.get("area"), function (err, re) {
          if (re !== undefined) {
            vm.closeDate = re.closeDate;
          } else {
            vm.closeDate = "";
          }

          if (vm.closeDate && vm.closeDate.getTime() >= row.invoiceDate.getTime()) {
            vm.$message({
              duration: 1000,
              message: "Already Closing Entry In Account!!",
              type: 'error'
            });
            return false;
          }

          if (row.status === "Active" || row.paymentNumber < 2) {
            vm.$confirm('This will permanently delete the file. Continue?', 'Warning', {
              confirmButtonText: 'OK',
              cancelButtonText: 'Cancel',
              type: 'warning'
            }).then(function () {
              Meteor.call("removePosInvoice", row._id, row, function (err, result) {
                if (!err) {
                  rows.splice(index, 1);
                  vm.$message({
                    message: row.invoiceDateName + "\u200B : " + row.invoiceNo + " " + vm.langConfig['removeSuccess'],
                    type: 'success'
                  });
                  Session.set("transactionActionNumber", (Session.get("transactionActionNumber") || 0) + 1);
                } else {
                  vm.$message({
                    type: 'error',
                    message: vm.langConfig['removeFail']
                  });
                }
              });
            }).catch(function () {
              vm.$message({
                type: 'info',
                message: 'Delete canceled'
              });
            });
          } else {
            vm.$message({
              type: 'error',
              message: 'Already Paid !!'
            });
          }
        });
      } else {
        if (row.status === "Active" || row.paymentNumber < 2) {
