                        });
                    }

                })
            } else {
                if ((row.status === "Active" || row.paymentNumber < 2) && row.transactionType !== "Invoice Sale Order") {
                    vm.customerOpt(row.customerDoc._id);
                    vm.termOpt();
                    vm.dialogUpdatePosInvoice = true;
                    vm.findPosInvoiceById(scope);
