                this.$jQuery('body').on('keydown', elem, this.inputHandler);
            })
        }
    },
    data() {
        return {
            chartAccountData: [],
            loading: false,
            searchData: '',
            isSearching: false,
            currentPage: 1,
            currentSize: 10,
            count: 0,
            dialogAddChartAccount: false,
            dialogUpdateChartAccount: false,
            dialogMapChartAccount: false,
            dialogMapFixedAsset: false,

