            Meteor.call('queryChartAccount', {
                q: val,
                filter: this.filter,
                options: {skip: skip || 0, limit: limit || 10}
            }, (err, result) => {
                if (!err) {
                    this.chartAccountData = result.content;
                    this.count = result.countChartAccount;
                }
                this.isSearching = false;
            });
        }, 300),
